import React, { useEffect, useState } from 'react';
import '../styles/style.css'


const Users = () => {

    const [data, setData] = useState([]);

    const handleDelete = async (userId) => {
        const confirmDelete = window.confirm("האם ברצונך למחוק את המשתמש?");
        if (confirmDelete) {
            try {
                const response = await axios.deleta(`http://localhost:1010/users/${userId}`, {
                    method: 'DELETE',
                });
                if (response.ok) {
                    // עדכן את רשימת המשתמשים לאחר המחיקה
                    setData(data.filter((user) => user._id !== userId));
                    console.log("User deleted successfully");
                } else {
                    console.error("Failed to delete user");
                }
            } catch (error) {
                console.error("Error deleting user:", error);
            }
        }
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:1010/users');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const result = await response.json();
                setData(result);
                } catch (error) {
                //! setError(error);
            } finally {
                //! setLoading(false);
            }
        }
        fetchData();
    },[]);

    return (
        <div>
            <h1>הלקוחות שלי</h1>
            <table border="1" style={{ width: '100%', textAlign: 'center' }}>
            <thead>
                <tr>
                    <th>שם פרטי</th>
                    <th>שם משפחה</th>
                    <th>טלפון</th>
                    <th>אימייל</th>
                    <th>עיר</th>
                    <th>רחוב</th>
                    <th>מס' בית</th>
                    <th>תאריך יצירת חשבון</th>
                    <th>פעיל</th>
                    <th>מחק</th>
                </tr>
            </thead>
            <tbody>
                {data.map((user, index) => (
                    <tr key={index}>
                        <td>{user.name.first}</td>
                        <td>{user.name.last}</td>
                        <td>{user.phone}</td>
                        <td>{user.email}</td>
                        <td>{user.address.city}</td>
                        <td>{user.address.street}</td>
                        <td>{user.address.houseNumber}</td>
                        <td>{user.createAt}</td>
                        <td>{user.isActive ? "כן" : "לא"}</td>
                        <td>
                            <button onClick={() => handleDelete(user._id)}>
                                    מחק משתמש
                                </button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
        </div>
    );
};

export default Users;
