import React, { useEffect, useState } from 'react';
import '../styles/home.css'; // ייבוא קובץ ה-CSS
import initialData from '../initial-data/initial-data.json';
import { useNavigate } from 'react-router-dom';


const Home = () => {
    const [classes, setClasses] = useState([]);
    const [bookingStatus, setBookingStatus] = useState({});

    const handleBooking = (index) => {
        setBookingStatus((prevStatus) => ({
        ...prevStatus,
        [index]: !prevStatus[index] // הופך את המצב של כל שיעור בין true ל-false
        }));
    };

    useEffect(() => {
        setClasses(initialData);
    }, []);


    return (
        <div>
            <h1>שיעורי ספורט בסטודיו</h1>
            <div>
                {classes.map((item, index) => (
                    <div key={index} className="classCard">
                        <h2>{item.className}</h2>
                        <p><strong>מורה:</strong> {item.teacherName}</p>
                        <p><strong>תאריך:</strong> {item.date}</p>
                        <p><strong>שעה:</strong> {item.time}</p>
                        <p><strong>משתתפים:</strong> {item.participants.join(', ')}</p>
                        <p><strong>סוג כיתה:</strong> {item.classType}</p>

                        <div>
                            <button onClick={() => handleBooking(index)}>
                                {bookingStatus[index] ? 'בטל תור' : 'תפוס תור'}
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
